import React from 'react';
import { PiCertificateLight } from "react-icons/pi";
import './tile.css';

const Tile = ({ text, icon, onClick }) => {
  return (
    <div className="dashboard-tile" onClick={onClick}>
      <div className="tile-content">
        {icon && <PiCertificateLight className="tile-icon" icon={icon} />} 
        <h2 className="tile-text">{text}</h2>
      </div>
    </div>
  );
};

export default Tile;
