import React, { useState } from 'react';
import './menubar.css';
import { useAuthContext } from '@asgardeo/auth-react';
import Button from '../Button/button';

const MenuBar = () => {
  const { signOut } = useAuthContext();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    signOut();
    console.log('Logout clicked');
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  return (
    <div className={`menu-bar ${mobileMenuOpen ? 'mobile-menu-open' : ''}`}>
      <p>Grama Check</p>
      <div className={`menu-items ${mobileMenuOpen ? 'hidden' : ''}`}>
        <Button style={{ backgroundColor: '#fff', color: '#BA68C8' }} onClick={handleLogout}>
          Logout
        </Button>
      </div>
      <div className={`mobile-menu ${mobileMenuOpen ? 'visible' : ''}`} onClick={toggleMobileMenu}>
        <div className="dot" />
        <div className="dot" />
        <div className="dot" />
      </div>
      <div className={`mobile-dropdown ${mobileMenuOpen ? 'visible' : ''}`}>
        <Button style={{ backgroundColor: '#fff', color: '#BA68C8' }} onClick={handleLogout}>
          Logout
        </Button>
      </div>
    </div>
  );
};

export default MenuBar;
